CREATE TABLE `pages` (
  `id` int(3) NOT NULL,
  `name` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL,
  `meta_d` varchar(255) CHARACTER SET utf32 NOT NULL,
  `meta_k` varchar(255) NOT NULL,
  `img` varchar(255) NOT NULL,
  `text` text NOT NULL,
  `text1` text NOT NULL,
  `text2` text NOT NULL,
  `text3` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `pages`
--

INSERT INTO `pages` (`id`, `name`, `title`, `meta_d`, `meta_k`, `img`, `text`, `text1`, `text2`, `text3`) VALUES
(1, 'index', 'Интернет-магазин климатической техники', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '<p>Наша компания предлагает Вам только лучшее оборудование. \nМы отобрали для Вас бренды с идеальным сочетанием цены и качества от бюджетных до элитных. \nМы всегда идем навстречу каждому клиенту и стараемся подстроиться под Ваши потребности и возможности.</p>\n<p>Чтобы не ошибиться в выборе при покупке сложнобытовой техники, такой как кондиционер, \nнужно долго и тщательно разбираться во всех тонкостях данного вопроса, поэтому наша команда всегда рада \nсэкономить Ваше время, силы и нервы и предоставить бесплатную консультацию специалиста по телефону или прямо на месте.</p>\n<p>При монтаже и обслуживании кондиционеров или любой другой техники наши монтажники используют только \nпроверенные временем материалы и инструменты. <a href=\"about.php\"> Узнайте больше</a></p>', 'Бесплатно доставим и поднимем на этаж приобретенное оборудование.', 'Первое сервисное обслуживание купленного и смонтированного нашей компанией оборудования со скидкой 10%.', 'Мы отобрали для Вас бренды с идеальным сочетанием цены и качества от бюджетных до элитных.'),
(2, 'about', 'О магазине', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '        <p>Наша компания предлагает Вам только лучшее оборудование, которое представлено  на климатическом рынке. Мы отобрали для Вас бренды с идеальным сочетанием цены и качества от бюджетных до элитных.  Мы всегда идем навстречу каждому клиенту и стараемся подстроиться под Ваши потребности и возможности.</p>\n        <p>Чтобы не ошибиться в выборе при покупке сложнобытовой техники, такой как кондиционер, нужно долго и тщательно разбираться во всех тонкостях данного вопроса, поэтому наша команда всегда рада сэкономить Ваше время, силы и нервы и предоставить бесплатную консультацию специалиста по телефону или замерщика прямо на месте.</p>\n        <p>При монтаже и обслуживании кондиционеров или любой другой техники наши монтажники используют только проверенные временем материалы и инструменты, что положительно сказывается на качестве выполненных работ.</p>\n        <p>Мы имеем большой опыт на рынке климатического оборудования и ручаемся за свою работу.</p>\n        <p>Бесплатные услуги:</p>\n        <ul class=\"check\">\n            <li>квалифицированная консультация специалиста нашей компании</li>\n            <li>выезд замерщика в удобное для Вас время</li>\n            <li>занос купленного у нас оборудования в квартиру</li>\n        </ul>\n        <p>Преимущества работы с нашей компанией это:</p>\n        <ul class=\"check\">\n            <li>гарантированно высокое качество оборудования</li>\n            <li>реально быстрый и профессиональный монтаж</li>\n            <li>квалифицированные сотрудники</li>\n            <li>слаженная работа всей команды</li>\n            <li>индивидуальный подход к каждому клиенту</li>\n            <li>системы скидок и бонусов</li>\n        </ul>\n        <p class=\"center italic\">Сотрудничество с нами - это всегда выгодно и удобно.</p>\n        <p class=\"center italic\">НАШ ОПЫТ РАБОТАЕТ НА ВАС.</p>', '', '', ''),
(3, 'delivery', 'Доставка и оплата', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '        <p>Доставка товара:</p>\n        <ul class=\"check\">\n            <li>по г. Новосибирск - бесплатно.</li>\n            <li>в отдаленные районы г. Новосибирск - 200 р.</li>\n            <li>по области - 10 руб./км (подробности у менеджера компании).</li>\n        </ul>', '<h1>Время доставки</h1>        \n<p>Всегда согласовывается с заказчиком и осуществляется в максимально удобное для него время, но в пределах с 09:00 до 21:00, с понедельника по субботу, воскресенье - выходной. \nЗа пределами этих сроков доставки действует <a href=\"http://aero/service/?id=3\">система доплат</a>, с которой Вы можете ознакомиться в разделе наших услуг.</p>', '<h1>Правила</h1>\n<ul class=\"check\">\n    <li>Клиент обязан принять, осмотреть (подписать акт приема-передачи оборудования) и оплатить оборудование в полном объеме в день доставки (монтажа).</li>\n    <li>Если нет претензий к качеству монтажа, клиент обязан подписать акт выполненных работа сразу после монтажа.</li>\n    <li>Подъем на этаж и занос в квартиру осуществляется только того оборудования, которое было куплено в нашей компании.</li>\n</ul>', ''),
(4, 'service', 'Услуги', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '', '', '', ''),
(5, 'faq', 'Вопросы и ответы', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '', '', '', ''),
(6, 'news', 'Новости', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '', '', '', ''),
(7, 'article', 'Статьи', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '', '', '', ''),
(8, 'contact', 'Контакты', 'интернет-магазин климатической техники', 'магазин, интернет-магазин, климат, кондиционеры, увлажнители, очистители, обогреватели', '', '', '', '', '');

